
-- SaaS Subscription Analytics - Azure SQL DDL + Seed Data
-- Run this on your Azure SQL DB

IF OBJECT_ID('dbo.invoices','U') IS NOT NULL DROP TABLE dbo.invoices;
IF OBJECT_ID('dbo.subscriptions','U') IS NOT NULL DROP TABLE dbo.subscriptions;
IF OBJECT_ID('dbo.plans','U') IS NOT NULL DROP TABLE dbo.plans;
IF OBJECT_ID('dbo.customers','U') IS NOT NULL DROP TABLE dbo.customers;

CREATE TABLE dbo.customers(
  customer_id        VARCHAR(36) NOT NULL PRIMARY KEY,
  email_hash         CHAR(64)    NOT NULL,
  country            VARCHAR(2)  NULL,
  created_at         DATETIME2   NULL,
  last_updated       DATETIME2   NOT NULL
);

CREATE TABLE dbo.plans(
  plan_id            VARCHAR(36) NOT NULL PRIMARY KEY,
  plan_name          VARCHAR(50) NOT NULL,
  monthly_price      DECIMAL(10,2) NOT NULL,
  tier               VARCHAR(20) NOT NULL, -- basic/pro/enterprise
  effective_from     DATE NOT NULL,
  effective_to       DATE NULL,
  last_updated       DATETIME2 NOT NULL
);

CREATE TABLE dbo.subscriptions(
  subscription_id    VARCHAR(36) NOT NULL PRIMARY KEY,
  customer_id        VARCHAR(36) NOT NULL,
  plan_id            VARCHAR(36) NOT NULL,
  status             VARCHAR(20) NOT NULL, -- trialing/active/paused/canceled
  start_date         DATE NOT NULL,
  end_date           DATE NULL,
  last_updated       DATETIME2 NOT NULL,
  CONSTRAINT FK_sub_customer FOREIGN KEY (customer_id) REFERENCES dbo.customers(customer_id),
  CONSTRAINT FK_sub_plan     FOREIGN KEY (plan_id)     REFERENCES dbo.plans(plan_id)
);

CREATE TABLE dbo.invoices(
  invoice_id         VARCHAR(36) NOT NULL PRIMARY KEY,
  customer_id        VARCHAR(36) NOT NULL,
  subscription_id    VARCHAR(36) NOT NULL,
  invoice_date       DATE NOT NULL,
  amount             DECIMAL(10,2) NOT NULL,
  currency           CHAR(3) NOT NULL DEFAULT 'USD',
  paid               BIT NOT NULL,
  failure_reason     VARCHAR(50) NULL,
  last_updated       DATETIME2 NOT NULL,
  CONSTRAINT FK_inv_customer FOREIGN KEY (customer_id)     REFERENCES dbo.customers(customer_id),
  CONSTRAINT FK_inv_subscription FOREIGN KEY (subscription_id) REFERENCES dbo.subscriptions(subscription_id)
);




